$mcpConfig = @{
    mcpServers = @{
        'google-drive' = @{
            command = 'npx'
            args = @('mcp-google-drive@latest')
            env = @{
                GOOGLE_SERVICE_ACCOUNT_KEY = '${GOOGLE_SERVICE_ACCOUNT_KEY}'
            }
            description = 'Google Drive MCP Server'
        }
    }
}

$cursorMcpPath = "$env:USERPROFILE\.cursor\mcp.json"

if (Test-Path $cursorMcpPath) {
    $backup = "$cursorMcpPath.backup.$(Get-Date -Format 'yyyyMMdd-HHmmss')"
    Copy-Item $cursorMcpPath $backup
    Write-Host "Backed up: $backup" -ForegroundColor Green
}

$mcpConfig | ConvertTo-Json -Depth 10 | Set-Content $cursorMcpPath
Write-Host "Γ£ô Google Drive MCP configured" -ForegroundColor Green
Write-Host "Config location: $cursorMcpPath" -ForegroundColor Cyan
Write-Host "`nCurrent config:" -ForegroundColor Cyan
Get-Content $cursorMcpPath

